// Performance Monitoring Data Collection
const executionData = $json;
const nodeId = executionData.nodeId;
const nodeName = executionData.nodeName;
const executionTime = executionData.executionTime;
const timestamp = new Date().toISOString();

// Performance thresholds
const thresholds = {
  responseTime: 2000, // 2 seconds
  apiLatency: 3000,   // 3 seconds
  memoryUsage: 100,   // 100MB
  errorRate: 0.05     // 5%
};

// Calculate performance metrics
const metrics = {
  timestamp: timestamp,
  nodeId: nodeId,
  nodeName: nodeName,
  executionTime: executionTime,
  isSlow: executionTime > thresholds.responseTime,
  isError: !!executionData.error,
  memoryUsage: executionData.memoryUsage || 0,
  apiCalls: executionData.apiCalls || 0,
  dataSize: executionData.dataSize || 0
};

// Add business metrics if available
if (executionData.callSid) {
  metrics.callSid = executionData.callSid;
  metrics.callerPhone = executionData.callerPhone;
  metrics.conversationState = executionData.conversationState;
  metrics.hasFollowUp = executionData.hasFollowUp;
  metrics.isReturningCaller = executionData.isReturningCaller;
}

// Add API-specific metrics
if (executionData.openaiCall) {
  metrics.openaiLatency = executionData.openaiLatency;
  metrics.openaiTokens = executionData.openaiTokens;
  metrics.openaiCost = executionData.openaiCost;
}

if (executionData.sheetsCall) {
  metrics.sheetsLatency = executionData.sheetsLatency;
  metrics.sheetsRows = executionData.sheetsRows;
}

return {
  metrics: metrics,
  thresholds: thresholds,
  alerts: {
    slowExecution: metrics.isSlow,
    highMemory: metrics.memoryUsage > thresholds.memoryUsage,
    apiSlow: metrics.openaiLatency > thresholds.apiLatency,
    errorDetected: metrics.isError
  },
  // Preserve original data
  ...executionData
};

// Performance Analytics and Alerts
const metrics = $json.metrics;
const alerts = $json.alerts;
const thresholds = $json.thresholds;

// Calculate performance trends
const performanceAnalysis = {
  timestamp: metrics.timestamp,
  nodeName: metrics.nodeName,

  // Performance indicators
  performance: {
    executionTime: metrics.executionTime,
    isSlow: metrics.isSlow,
    performanceScore: Math.max(0, 100 - (metrics.executionTime / thresholds.responseTime) * 100)
  },

  // API performance
  api: {
    openaiLatency: metrics.openaiLatency || 0,
    sheetsLatency: metrics.sheetsLatency || 0,
    totalApiCalls: metrics.apiCalls,
    apiScore: metrics.openaiLatency ? Math.max(0, 100 - (metrics.openaiLatency / thresholds.apiLatency) * 100) : 100
  },

  // Resource usage
  resources: {
    memoryUsage: metrics.memoryUsage,
    dataSize: metrics.dataSize,
    resourceScore: Math.max(0, 100 - (metrics.memoryUsage / thresholds.memoryUsage) * 100)
  },

  // Business metrics
  business: {
    callSid: metrics.callSid,
    conversationState: metrics.conversationState,
    hasFollowUp: metrics.hasFollowUp,
    isReturningCaller: metrics.isReturningCaller
  }
};

// Generate alerts
const alertMessages = [];
if (alerts.slowExecution) {
  alertMessages.push(`âš ï¸ Slow execution: ${metrics.nodeName} took ${metrics.executionTime}ms`);
}
if (alerts.highMemory) {
  alertMessages.push(`âš ï¸ High memory usage: ${metrics.memoryUsage}MB`);
}
if (alerts.apiSlow) {
  alertMessages.push(`âš ï¸ Slow API call: OpenAI took ${metrics.openaiLatency}ms`);
}
if (alerts.errorDetected) {
  alertMessages.push(`ðŸš¨ Error detected in ${metrics.nodeName}`);
}

// Calculate overall health score
const healthScore = Math.round(
  (performanceAnalysis.performance.performanceScore + 
   performanceAnalysis.api.apiScore + 
   performanceAnalysis.resources.resourceScore) / 3
);

return {
  performanceAnalysis: performanceAnalysis,
  alerts: alertMessages,
  healthScore: healthScore,
  isHealthy: healthScore >= 80,
  timestamp: metrics.timestamp,
  // Preserve original data
  ...$json
};

// Dashboard Report Generation
const performanceAnalysis = $json.performanceAnalysis;
const alerts = $json.alerts;
const healthScore = $json.healthScore;
const isHealthy = $json.isHealthy;

// Generate dashboard report
const dashboardReport = {
  timestamp: performanceAnalysis.timestamp,
  summary: {
    healthScore: healthScore,
    status: isHealthy ? 'ðŸŸ¢ Healthy' : 'ðŸ”´ Needs Attention',
    totalAlerts: alerts.length,
    nodeName: performanceAnalysis.nodeName
  },

  performance: {
    executionTime: `${performanceAnalysis.performance.executionTime}ms`,
    performanceScore: `${performanceAnalysis.performance.performanceScore.toFixed(1)}%`,
    isSlow: performanceAnalysis.performance.isSlow ? 'Yes' : 'No'
  },

  api: {
    openaiLatency: performanceAnalysis.api.openaiLatency ? `${performanceAnalysis.api.openaiLatency}ms` : 'N/A',
    sheetsLatency: performanceAnalysis.api.sheetsLatency ? `${performanceAnalysis.api.sheetsLatency}ms` : 'N/A',
    apiScore: `${performanceAnalysis.api.apiScore.toFixed(1)}%`,
    totalCalls: performanceAnalysis.api.totalApiCalls
  },

  resources: {
    memoryUsage: `${performanceAnalysis.resources.memoryUsage}MB`,
    dataSize: `${performanceAnalysis.resources.dataSize}KB`,
    resourceScore: `${performanceAnalysis.resources.resourceScore.toFixed(1)}%`
  },

  business: {
    callSid: performanceAnalysis.business.callSid || 'N/A',
    conversationState: performanceAnalysis.business.conversationState || 'N/A',
    hasFollowUp: performanceAnalysis.business.hasFollowUp ? 'Yes' : 'No',
    isReturningCaller: performanceAnalysis.business.isReturningCaller ? 'Yes' : 'No'
  },

  alerts: alerts
};

// Generate formatted reports
const smsReport = `Performance Report\n\nHealth: ${dashboardReport.summary.status}\nScore: ${dashboardReport.summary.healthScore}%\nNode: ${dashboardReport.summary.nodeName}\nExecution: ${dashboardReport.performance.executionTime}\nAlerts: ${dashboardReport.summary.totalAlerts}`;

const emailReport = `Performance Dashboard Report\n\nTimestamp: ${dashboardReport.timestamp}\nNode: ${dashboardReport.summary.nodeName}\nHealth Score: ${dashboardReport.summary.healthScore}%\nStatus: ${dashboardReport.summary.status}\n\nPerformance Metrics:\n- Execution Time: ${dashboardReport.performance.executionTime}\n- Performance Score: ${dashboardReport.performance.performanceScore}\n- Slow Execution: ${dashboardReport.performance.isSlow}\n\nAPI Metrics:\n- OpenAI Latency: ${dashboardReport.api.openaiLatency}\n- Sheets Latency: ${dashboardReport.api.sheetsLatency}\n- API Score: ${dashboardReport.api.apiScore}\n- Total API Calls: ${dashboardReport.api.totalCalls}\n\nResource Usage:\n- Memory Usage: ${dashboardReport.resources.memoryUsage}\n- Data Size: ${dashboardReport.resources.dataSize}\n- Resource Score: ${dashboardReport.resources.resourceScore}\n\nBusiness Context:\n- Call SID: ${dashboardReport.business.callSid}\n- Conversation State: ${dashboardReport.business.conversationState}\n- Has Follow-up: ${dashboardReport.business.hasFollowUp}\n- Returning Caller: ${dashboardReport.business.isReturningCaller}\n\nAlerts:\n${dashboardReport.alerts.map(alert => `- ${alert}`).join('\n')}\n\nRecommendations:\n${generateRecommendations(dashboardReport)}`;

function generateRecommendations(report) {
  const recommendations = [];

  if (report.performance.isSlow === 'Yes') {
    recommendations.push('- Optimize node execution time');
  }
  if (parseFloat(report.api.apiScore) < 80) {
    recommendations.push('- Investigate API latency issues');
  }
  if (parseFloat(report.resources.resourceScore) < 80) {
    recommendations.push('- Monitor memory usage');
  }
  if (report.alerts.length > 0) {
    recommendations.push('- Address active alerts');
  }

  return recommendations.length > 0 ? recommendations.join('\n') : '- System performing well';
}

return {
  dashboardReport: dashboardReport,
  smsReport: smsReport,
  emailReport: emailReport,
  timestamp: performanceAnalysis.timestamp,
  // Preserve original data
  ...$json
};
