// Function Node: Conversation Flow Logic
const webhookData = $json.webhookData;
const callerInfo = $json;
const speechResult = webhookData.SpeechResult;
const callSid = webhookData.CallSid;

// Determine conversation state based on caller info and speech
let conversationState = 'initial';
let followUpQuestion = null;

if (callerInfo.isReturningCaller) {
  if (speechResult && speechResult.toLowerCase().includes('yes')) {
    conversationState = 'extract_project_details';
  } else {
    conversationState = 'returning_caller_greeting';
  }
} else {
  conversationState = 'new_caller_greeting';
}

// Set conversation cues based on state
let conversationCues = callerInfo.conversationCues;

if (conversationState === 'new_caller_greeting') {
  conversationCues = {
    welcomeMessage: "Hi! I'm here to help you generate a construction contract. What's the name of your business?",
    contactQuestion: "What's the best way to get this to you â€” text or email?"
  };
} else if (conversationState === 'returning_caller_greeting') {
  conversationCues = {
    welcomeMessage: callerInfo.conversationCues.welcomeMessage,
    contactConfirmation: callerInfo.conversationCues.contactConfirmation
  };
}

return {
  conversationState,
  followUpQuestion,
  callerInfo: conversationCues,
  callSid: callSid,
  speechResult: speechResult,
  isReturningCaller: callerInfo.isReturningCaller
};

// Function Node: Twilio Response Templates
const conversationState = $json.conversationState;
const followUpQuestion = $json.question;
const callerInfo = $json.callerInfo;
const extractedData = $json.extractedData;

// Base TwiML template
const createTwiML = (message, gatherOptions = {}) => {
  const defaultGather = {
    input: 'speech',
    timeout: 10,
    speechTimeout: 'auto',
    action: '/webhook/process-speech',
    method: 'POST'
  };

  const gather = { ...defaultGather, ...gatherOptions };

  return `<?xml version="1.0" encoding="UTF-8"?>
<Response>
  <Say voice="alice">${message}</Say>
  <Gather input="${gather.input}" 
          timeout="${gather.timeout}" 
          speechTimeout="${gather.speechTimeout}"
          action="${gather.action}" 
          method="${gather.method}">
    <Say voice="alice">I didn't catch that. Could you repeat?</Say>
  </Gather>
  <Say voice="alice">Thanks for calling. We'll follow up with you soon.</Say>
  <Hangup/>
</Response>`;
};

// Response templates for different scenarios
const responseTemplates = {
  newCallerGreeting: () => {
    const message = `Hi! I'm here to help you generate a construction contract. ${callerInfo.welcomeMessage}`;
    return createTwiML(message);
  },

  returningCallerGreeting: () => {
    const message = callerInfo.welcomeMessage;
    return createTwiML(message);
  },

  followUpQuestion: () => {
    const message = followUpQuestion;
    return createTwiML(message);
  },

  completion: () => {
    const message = `Perfect! I've got all the details for your ${extractedData.project_type} project. I'll generate the contract and send it to you right away. Thanks for calling!`;
    return createTwiML(message, { action: '/webhook/end-call' });
  },

  error: (errorMessage = "I'm having trouble processing that. Let me start over.") => {
    return createTwiML(errorMessage, { action: '/webhook/restart' });
  }
};

// Determine which template to use
let response;
switch (conversationState) {
  case 'new_caller_greeting':
    response = responseTemplates.newCallerGreeting();
    break;
  case 'returning_caller_greeting':
    response = responseTemplates.returningCallerGreeting();
    break;
  case 'follow_up':
    response = responseTemplates.followUpQuestion();
    break;
  case 'complete':
    response = responseTemplates.completion();
    break;
  case 'error':
    response = responseTemplates.error();
    break;
  default:
    response = responseTemplates.error();
}

return {
  twiML: response,
  conversationState,
  callerInfo,
  followUpQuestion
};
