const extractedData = $json.message.content;
const speechResult = $json.speechResult;

let parsedData;
try {
  parsedData = JSON.parse(extractedData);
} catch (error) {
  return {
    error: 'PARSE_ERROR',
    message: 'Failed to parse OpenAI response',
    originalResponse: extractedData
  };
}

const missingFields = [];

if (!parsedData.business_name) missingFields.push('business_name');
if (!parsedData.project_type) missingFields.push('project_type');
if (!parsedData.project_address) missingFields.push('project_address');
if (!parsedData.budget) missingFields.push('budget');
if (!parsedData.payment_terms) missingFields.push('payment_terms');
if (!parsedData.start_date) missingFields.push('start_date');
if (!parsedData.preferred_contact_method) missingFields.push('preferred_contact_method');

const followUpQuestions = {
  business_name: "What's the name of your business?",
  project_type: "What type of project is this? (e.g., kitchen remodel, bathroom renovation, deck build)",
  project_address: "What's the project address?",
  budget: "What's the total budget for this project?",
  payment_terms: "How would you like to structure the payments? (e.g., 50% upfront, 50% on completion)",
  start_date: "When would you like to start this project?",
  preferred_contact_method: "What's the best way to get the contract to you â€” text or email?"
};

if (missingFields.length > 0) {
  const nextField = missingFields[0];
  return {
    hasFollowUp: true,
    question: followUpQuestions[nextField],
    missingField: nextField,
    remainingFields: missingFields.slice(1),
    extractedData: parsedData,
    conversationState: 'follow_up'
  };
}

return {
  hasFollowUp: false,
  isComplete: true,
  extractedData: parsedData,
  conversationState: 'complete'
};
